          <div className="animate-pulse">
            <div className="w-20 h-20 rounded-full bg-[#333] mx-auto mb-4" />
            <div className="h-6 bg-[#333] rounded w-32 mx-auto mb-2" />
            <div className="h-4 bg-[#333] rounded w-24 mx-auto" />
